mapper.update:
UPDATE user
auth=#{user.auth}, classNum=#{user.classNum}, createDay=#{user.createDay}, grade=#{user.grade}, image=#{user.image}, isEffective=#{user.isEffective}, mobile=#{user.mobile}, openId=#{user.openId}, password=#{user.password}, realname=#{user.realname}, studentId=#{user.studentId}, userId=#{user.userId}, username=#{user.username},
WHERE userId = #{userId};

mapper.select:
SELECT 
	 
FROM 
	user a
WHERE 
	userId = #{userId}
LIMIT 1;

SELECT

FROM 
	user a

	
		AND a.userId=#{map.uId}
	
	
		AND a.mobile=#{map.mobile}
	
	
		AND a.username=#{map.username}
	
	
		AND a.studentId=#{map.studentId}
	
	
		AND a.isEffective=#{map.isEffective} 
	


ORDER BY 
	a.${map.order} ${map.aOrS} 


limit #{map.start},#{map.limit}

SELECT

FROM
	user a

	
		AND a.userId=#{map.uId}
	
	
		AND a.mobile=#{map.mobile}
	
	
		AND a.username like CONCAT ('%', #{map.username}, '%')
	
	
		AND a.studentId=#{map.studentId}
	
	
		AND a.isEffective=#{map.isEffective}
	


ORDER BY
	a.${map.order} ${map.aOrS}


limit #{map.start},#{map.limit}

		SELECT
			count(*)
		FROM 
